  public void activar (String idPuesto, boolean mld, boolean slo)
  {
    setEstado ("P");
    setIdpuesto(idPuesto);
    if (mld)
      setAsigmld("S");
    if (slo)
      setAsigslo("S");
    
  }

  /**
   * Activar una línea para el puesto.
   * Activar líneas adicionales si es que son de una sola línea del mismo artículo
   * @param slo activar para el silo
   * @param mld activar para el miniload
   * @param idPuesto
   * TODO: Siempre está activando todos los que tienen la misma línea. Controlar ?
   */
  public void activarLinia (String idPuesto, boolean mld, boolean slo)
  {
    activar(idPuesto, mld, slo);
    
    // Ver si la orden es de una sóla línea
    SgavldocunicalineaViewRowImpl row = (SgavldocunicalineaViewRowImpl)getSgavldocunicalineaView();
    
    if (row != null)
      // activar otras órdenes de una línea del mismo artículo
      row.asignarDocsUnicaLineaMismoArticulo(idPuesto);
    
    
  }
  

