  /**
   * Comprovar si la línia es del MINI o el MAXI (SLO)
   * 
   */
  public boolean isPreparacionSLO ()
  {
    String queryResult = getApplicationModule().getTransaction().
                    dumpQueryResult("select idtipalm from SGAVSCDOCPENDIENTE_SLO where " +
                    "iddoc = '" + getIddoc() + "' and idlin='" + getIdlin() + "'", 
                      "oracle.jbo.server.QueryDumpTab",
                          null);
    int n = queryResult.indexOf("\n")+1;
    String silo = queryResult.substring(n).trim();
    
    if (silo != null && silo.startsWith("SLO"))
      return true;
    
    return false;
    

  }

  /**
   * Actualitza l'estat de la linia i el puesto
   * segons el tipus d'estat passat com a parametre
   * @see #activarLinia
   */
  public void actualitzarLinia(String estat, String puesto)
  {
    setEstado(estat);
    if (estat.equals("P"))
      activarLinia(puesto, true, true);
  }

