  private String getRotacionMac()
  {
    SgaordtranViewRowImpl row = (SgaordtranViewRowImpl)getSgaordtranView();
    return row.getRotacionMac();
  }
  
  private String getSiguienteRotacion(String rotacion, String original)
  {
  char [] rot = { rotacion.charAt(0)};
  if (original.equals("C"))
  {
      rot [0]--;
      if (rot [0] < 'A')
        rot [0] = 'C';
  }
  else
    {
      rot [0]++; // A->B->C
      if (rot [0] > 'C')
        rot [0] = 'A';
    }
  return (new String(rot));  
  }
  
  /**
   * Buscar misma rotación
   * Si no encontrado, buscar por siguiente rotacion
   **/
   private SgavubicacionlibresloViewRowImpl getUbicacionLibrePorRotacion()
   {
    oracle.jbo.RowIterator rows = getSgavubicacionlibresloView();

    String rotacion = getRotacionMac();
    String originalRotacion = new String (rotacion);
    int prioridad = 0; // Para controlar que no se salta mejor ubicacion por respetar la rotacion
    
    for (int i = 0 ; i < 3 ; i++ ) 
    {
    rows.reset();
    while (rows.hasNext()) 
      {
         SgavubicacionlibresloViewRowImpl row = (SgavubicacionlibresloViewRowImpl)rows.next();
         if (row.getPrioridad().intValue() < prioridad)
         {
           // Se esta mirando peor opcion
           row = (SgavubicacionlibresloViewRowImpl)rows.previous();
           return row;
         }
         else
          prioridad = row.getPrioridad().intValue();
         
         if (row.getRotacion().equals(rotacion))
         {
          return row;
         }
      }
      // Mirar siguiente rotacion
      rotacion = getSiguienteRotacion(rotacion, originalRotacion);
    }
    throw new RuntimeException ("No se ha encontrado ubicacion disponible para mac: " + getIdmac() + " en pasillo: " + getIdpasillo());
    
     
   }
   
  /**
   * Poner la primera ubicación adecuado que se encuentra en este pasillo como destino del mac
   * @return Si falla (porque falla ?) devuelve false
   * @param idTraslo el traslo que ejecutará la orden
   * Michael 17.02.2009 Modificado para buscar de la misma rotacion
   */
  public boolean setUbicacionDestinoMac(Number idTraslo)
  {

    SgavubicacionlibresloViewRowImpl row = getUbicacionLibrePorRotacion();
    if (row != null)
    {
      SgaordtranViewRowImpl ordtranRow = (SgaordtranViewRowImpl)getSgaordtranView();
      ordtranRow.setDestinoTraslo(idTraslo, row.getIdubi(), row.getPosicion());
      return true;
    }
    return false;
  }
  
