  public void setReenviar(boolean nuevo)
  {
    if (nuevo)
      getSgaordtran().setEstado ("N");
    else
      getSgaordtran().setEstado ("2");
      
    // Michael 22.02.2006 Si reenvia, resetea el error
    getSgaordtran().setCoderror(new Number(0));
    
  }
  
  public void setBorrado(boolean inspeccionarUbicacion)
  {
    getSgaordtran().resetOrdtran(inspeccionarUbicacion);

  }
  
  
  /**
   * Si es una orden del silo, devolver dos traslos
   * Si es del miniload, devolver null
   * @return 
   */
  public Vector getTraslos ()
  {
    if (!getSgamac().isMiniload())
    {
      Vector traslos = new Vector();
      
      traslos.add("Traslo 1");
      traslos.add("Traslo 2");
      return traslos;
    }
    else
      return null;
  }
  
  /**
   * Modificar el traslo y actualiza la orden para que se reenvie
   * @param idTraslo
   */
  public void changeTraslo(int idTraslo)
  {
    getSgaordtran().setIdtraslo(new Number(idTraslo));
    setReenviar(false);
  }
  
  private String getIdubiFromPosPlc(String posPlc)
  {
    DBTransaction txn = (DBTransaction)getApplicationModule().getTransaction();
    ViewObject vo = txn.createViewObject("es.sysmap.interflex.model.dmc.SgavposicionplcView");
    vo.setWhereClause(null);
    vo.setWhereClause("posplc = " + posPlc);
    vo.executeQuery();
    SgavposicionplcViewRowImpl row = (SgavposicionplcViewRowImpl)vo.first();
    if (row == null)
      throw new NullPointerException ("No se ha trobat la posició del plc: " + posPlc);

    return row.getIdubi();
   
  }
  
  
  
  public void simularAvis (int codigoError, String posPlc)
  {
    getSgaordtran().setAvis(codigoError, getIdubiFromPosPlc(posPlc));
  }


  public void simularPeti (int codigoError, String posPlc)
  {
    getSgaordtran().setPeti(codigoError, getIdubiFromPosPlc(posPlc));
  }

  public Vector getDestinoPlc()
  {
    RowIterator rows = getSgavposicionplcView();
    Vector destinos = new Vector();
    rows.reset();
    for (int i = 0; i < rows.getRowCount(); i++)
    {
      SgavposicionplcViewRowImpl row = (SgavposicionplcViewRowImpl)rows.next();
      destinos.add(row.getPosplc().stringValue() + " " + row.getDescrip()); 
    }
    
    return destinos;
    
  }

